@import './variables';

/********* MAIN *********/

.main {
    width: 100%;
    height: 100%;
    background: $black;
    color: $white;
    display: grid;
    grid-template-columns: repeat(5, 1fr);
    grid-template-rows: repeat(5, 1fr);
}

.main__box {
    border-right: 0px solid rgba($main2, .1);
    height: 0%;
    grid-row: 1 / span 5; 
    z-index: 1; 
}

.main__box:nth-child(1) {
    grid-column: 1;
}

.main__box:nth-child(2) {
    grid-column: 2;
}

.main__box:nth-child(3) {
    grid-column: 3;
}

.main__box:nth-child(4) {
    grid-column: 4;
}

.main__box::after {
    content: '';
    position: absolute;
    width: 0%;
    height: 100%;
    bottom: 0;
    background: $black;
}

.main__logo {
    grid-column: 1;
    grid-row: 1;
    padding: 70px 0 0 70px;
    @media all and (max-width: $mobile) {
        padding: 10px 0 0 20px;
    }
}

.logo__h1 {
    width: 190px;
    visibility: hidden;
    font-family: $font1;
    text-transform: uppercase;
    font-size: 1.2em;
    font-weight: 500;
    margin-bottom:-10px;
    position: relative;
    transition: all ease 500ms;
}

.logo__h1::after {
    @include after-black;
}

.main__menu {
    z-index: 2;
    visibility: hidden;
    position: relative;
    grid-column: 5;
    grid-row: 1;
    padding: 70px 0 0 70px;
    border-right: 1px solid $lightgray;
    display: flex;
    justify-content: flex-end;
    padding: 70px 70px 0 0;
    @media all and (max-width: $mobile) {
        padding: 20px 20px 0 0;
    }
}

.main__menu::after {
    @include after-black;
}

.menu__h2 {
    font-family: $font1;
    height: 20px;
    text-transform: uppercase;
    font-size: .8em;
    font-weight: 600;
    position: relative;
    transition: all ease 500ms;
}

.menu__h2::before {
    content: '';
    position: absolute;
    width: 57px;
    height: 2px;
    left: -82px;
    top: 7px;
    background: $white;
    transition: all ease 500ms;
}

.menu__h2::after {
    content: '';
    transition: all ease 500ms;
    position: absolute;
    width: 0px;
    height: 2px;
    left: -35px;
    top: 4px;
}

.menu__h2:hover {
    &::before {
        width: 20px;
        left: -35px;
        top: 9px;
        background: $main1;
    }
    &::after {
        width: 20px;
        background: $main1;
    }
}

// MAIN BOTOM

.main__bottom {
    visibility: hidden;
    position: fixed;
    bottom: 0;
    left: 20%;
    width: 21%;
    background-color: $main1;
    padding: 70px 0 0 70px;
    display: flex;
    justify-content: flex-start;
    align-items: center;
    padding: 35px;
    @media all and (max-width: $mobile) {
        display: none;
    }
}

.main__bottom::after {
    @include after-black;
}

.bottom__numbers {
    width: 175px;
    display: flex;
    justify-content: space-between;
    position: relative;
}

.bottom__num:first-child::after {
    content: '';
    position: absolute;
    width: 60px;
    height: 1px;
    left: 50px;
    top: 7px;
    background: $black;
}

.bottom__num {
    color: $black;
    font-family: $font1;
    font-size: .7em;
    font-weight: 600;
}

// MAIN ITEMS LEFT

.main__items {
    position: relative;
    grid-column: 1;
    grid-row: 3;
    padding: 70px 0 0 70px;
    display: flex;
    justify-content: flex-start;
    @media all and (max-width: $mobile) {
        // padding: 10px 0 0 20px;
