@import url('https://fonts.googleapis.com/css?family=Montserrat:100,200,300,400,500,600,700|Roboto:100,300,400,500,700&display=swap');
@import url('https://fonts.googleapis.com/css?family=Permanent+Marker&display=swap');

$black: #191A1E;
$white: #fffffe;
$lightgray: rgba(255, 255, 255, .1);
$darkgray: rgba(0, 0, 0, .8);

$main1: #e41143;
$main2: #fffffe;

$font1: 'Roboto', sans-serif;
$font2: 'Montserrat', sans-serif;
$font3: 'Permanent Marker', cursive;

$mobile: 920px;
$smallScreen: 1900px;
$screenHeight: 650px;

$slide1: '.main__content[data-slide="1"]';
$slide2: '.main__content[data-slide="2"]';
$slide3: '.main__content[data-slide="3"]';
$slide4: '.main__content[data-slide="4"]';

@mixin after-black {
    content: "";
    position: absolute;
    right: 0;
    top: 0px;
    width: 100%;
    height: 100%;
    background: $black;
}

$techSlide: -35;
$circleSpeed: 50s;
$rectangleSpeed: 20s;

