    }
    .project__details-header {
        display: flex;
        font-weight: 100;
        .project__details-left {
            width: 60%;
            padding-right: 2em;
        }
        .project__details-right {
            width: 40%;
            padding-right: 2em;
        }
        .project__details-title {
            font-family: $font2;
            margin-top: 0;
            margin-bottom: .5em;
            font-size: 2em;
            color: white;
        }
        .project__details-desc {
            font-family: $font2;
            color: white;
            font-weight: 300;
            line-height: 1.5em;
        }
        .project__details-header {
            font-family: $font1;
            margin: 0;
            text-transform: uppercase;
            font-weight: 600;
            font-size: .9em;
            color: white;
        }
        .project__details-divider {
            margin: 1em 0;
            width: 100%;
            height: 1px;
            background: white;
        }
        .project__details-buttons {
            margin-top: 1em;
            display: flex;
            flex-wrap: wrap;
        }
    }
    .project__details-images {
        display: flex;
        justify-content: space-evenly;
        background: slategray;
        img {
            width: 47%;
            margin: 2%;
            &:first-child {
                margin-right: 1%;
            }
            &:last-child {
                margin-left: 1%;
            }
        }
    }
    .project__details-close {
        position: absolute;
        right: 5%;
        border: 1px solid white;
        padding: .7em;
        border-radius: 50%;
        transition: all 250ms ease;
        font-weight: 600;
        &:hover {
            cursor: pointer;
            background: white;
            color: slategray;
        }
    }
}

